import React from "react";
import Product from "./Product";
import { useState } from "react";

const products = [
    {
      id: 1,
      title: 'Book',
      description: 'On-demand printed copy of given document.',
      votes: '1',
      Avatar: 'a.jpg',
      Image: 'data:image/jpeg;base64,',
    },
    {
      id: 2,
      title: 'Bird Lamp',
      description: 'We make Any bird shape you like.',
      votes: '2',
      Avatar: 'c.png',
      Image: 'data:image/jpeg;base64,',
    },
    {
      id: 3,
      title: 'White-Jug Lamp',
      description: 'We already have this in three colors.',
      votes: '3',
      Avatar: 'b.jpg',
      Image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzDzQfMOmoFAoV61QMCFPm8qBaM5bSwloKug&usqp=CAU',
    },
    {
      id: 4,
      title: 'Ball lamp',
      description: 'Small pertions with multiple light.',
      votes: '4',
      Avatar: 'c.png',
      Image: 'data:image/jpeg;base64,',
    },
  ];

  function LoadList(){

    const list = products.map((prod)=>(
        <Product 
        id = {prod.id}
        title={prod.title} 
        description={prod.description}
        Image={prod.Image}
        votes={prod.votes}
        Avatar={prod.Avatar}
        upvote={addProduct}
        />

    ));

    const [,updateState] = React.useState();
    const forceUpdate = React.useCallback(() => updateState({}), []);
  
    const [votes, setVoteCount] = useState(products);
    
    function addProduct(id) {
      let index = products.findIndex((obj) => obj.id === id);
      products[index].votes++;
      setVoteCount(votes);
      products.sort((a, b) => b.votes - a.votes);
      console.log(products);
      forceUpdate();
    }

    return(
        <div>
            {list}
        </div>
    );
    
  }

  export default LoadList;
  //export default products;